mapper.insert:
insert into tb_attendence(
   no, att_status, att_leave_time, att_no
 )values(
    #{no},#{attStatus},#{attLeaveTime}, s_att_no.nextval
 )

	insert into tb_attendence(
		no, att_status, att_enter_time, att_no
    )values(
    	#{no},#{attStatus},#{attEnterTime}, s_att_no.nextval
    )

mapper.update:
update tb_attendence
   set att_leave_time = #{attLeaveTime},
       att_status = #{attStatus}
 where att_no = #{attNo}

mapper.select:
select count(*)
  from tb_attendence
 where no = #{no}
   and att_status = #{attStatus}

select * from tb_attendence where no = #{no}
and to_char(att_leave_time,'YYYY-MM-DD') >= TRUNC(#{attLeaveTime},'MM') and to_char(att_leave_time,'YYYY-MM-DD') <= LAST_DAY(#{attLeaveTime})
order by att_leave_time

select * from tb_attendence where no = #{no}
and to_char(att_enter_time,'YYYY-MM-DD') >= TRUNC(#{attEnterTime},'MM') and to_char(att_enter_time,'YYYY-MM-DD') <= LAST_DAY(#{attEnterTime})
order by att_enter_time
